import { Component } from '@angular/core';
import { map } from 'rxjs';
// import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import '../../../node_modules/bootstrap/dist/js/bootstrap.bundle.js';

let checkedArray: any[] = [];
let mapper= new Map();
mapper.set("id_blore","Bangalore");
mapper.set("id_hyd","Hyderabad");
mapper.set("id_kol","Kolkata");
@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
  
  onChange(event:any){
    
    let element=event.target;
    let eid=element.id;
    
    // alert(eid);
    if(element.checked && !checkedArray.includes(mapper.get(eid))){
      // console.log("it is checked");
      
        checkedArray.push(mapper.get(eid));
    }
    else if(!element.checked){
      // console.log("unchecked");
      if(checkedArray.includes(mapper.get(eid)))
      {
        for (let i = 0; i < checkedArray.length; i++) {
          console.log(mapper.get(eid));
          if (checkedArray[i] == mapper.get(eid)) 
          {
            // console.log("deleting");
              checkedArray.splice(i,1);
          }
        }
      }
    }
    console.log(checkedArray);
    
  }
}
